  var newArr = List[Int]()
  //
  //nums.map(_.toInt).toList match {
  //  case x :: y :: z :: tail if z == y + 1 && y == x + 1 => x :: y :: newArr
  //  case _ =>
  //}

  @tailrec
  def f(l: List[Int]): Unit = {
    l match {
      case x :: y :: z :: tail if z == y + 1 && y == x + 1 =>
        newArr = newArr :+ x
        f(z :: tail)
      case x :: y :: z :: tail =>
        newArr = newArr :+ x
        f(y :: (z :: tail))
      case arr =>
        newArr = newArr ++ arr
    }
  }
nums.map(_.toInt).foreach(print)
println()
  f(nums.map(_.toInt).toList)

  println(newArr.length)
  newArr.foreach(print)
  println()
